class EmailPopup extends HTMLElement {
  connectedCallback() {
    this.innerHTML = 
    `<aside class="email_pop">
      <div class="inner_wrap flex">
        <h3 class="hide">이메일 발송 팝업</h3>
        <p class="title">이메일 발송</p>
        <p class="cont">아래 필수 사항을 모두 작성하신 후 발송 버튼을 눌러주세요.</p>

        <form id="emailForm" class="grid">
          <div class="input_box">
            <label for="name" data-req="*필수">기업명</label>
            <input type="text" id="name" name="name" placeholder="ex. OO소프트">
          </div>

          <div class="input_box">
            <label for="phone" data-req="*필수">연락처</label>
            <input type="text" id="phone" name="phone" placeholder="ex. 010-1234-1234">
          </div>

          <div class="input_box">
            <label for="email">이메일</label>
            <input type="text" id="email" name="email" placeholder="ex. pubsj2022@gmail.com">
          </div>

          <div class="input_box">
            <label for="text" data-req="*필수">메시지</label>
            <textarea id="text" name="text" placeholder="ex. 간단한 메시지 작성"></textarea>
          </div>
        </form>

        <div class="btn_wrap flex">
          <button type="button" class="btn submit" onclick="sendEmail(this);">발송</button>
          <button type="button" class="btn close" onclick="closeEmailForm();">닫기</button>
        </div>
      </div>
    </aside>`;

    this.classList.add('components');
  }
}

